function SetCookie(e,o){var t=30,n=new Date;n.setTime(n.getTime()+24*t*60*60*1e3),document.cookie=e+"="+escape(o)+";expires="+n.toGMTString()}function delCookie(e){var o=new Date;o.setTime(o.getTime()-1);var t=getCookie(e);null!=t&&(document.cookie=e+"="+t+";expires="+o.toGMTString())}function getCookie(e){var o=document.cookie.match(new RegExp("(^| )"+e+"=([^;]*)(;|$)"));return null!=o?unescape(o[2]):null}